import React from "react";
import Player from "./choosePlayer"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      board: Array(9).fill(null),
      player: null,
      winner: null,
    };
  }

  setPlayer(player) {
    this.setState({
      player
    })
  }

  checkWinner() {
    let winLines = [
      ["0", "1", "2"],
      ["3", "4", "5"],
      ["6", "7", "8"],
      ["0", "3", "6"],
      ["1", "4", "7"],
      ["2", "5", "8"],
      ["0", "4", "8"],
      ["2", "4", "6"],
    ];
    for (let index = 0; index < winLines.length; index++) {
      const [a, b, c] = winLines[index];
      if (
        this.state.board[a] &&
        this.state.board[a] === this.state.board[b] &&
        this.state.board[a] === this.state.board[c]
      ) {
        alert("winner");
        this.setState({
          winner: this.state.player
        }) 
      }
    }
  }

  handleClick = (index) => {
    if(this.state.player && !this.state.winner){
      let newBoard = this.state.board;
      if (this.state.board[index] === null ) {
        newBoard[index] = this.state.player;
        this.setState({
          board: newBoard,
          player: this.state.player === "X" ? "O" : "X",
        });
        this.checkWinner()
      }
    }
  };
  render() {
    const myBox = this.state.board.map((box, index) => (
      <div className="box" key={index} onClick={() => this.handleClick(index)}>
        {box}
      </div>
    ));

    let status = this.state.player ? <h2>Next Player is: {this.state.player}</h2> : <Player player={(e) => this.setPlayer(e)}/>
    return (
      <div className="container">
        <h1>Tic Tac Toe App</h1>

        {status}

        <div className="board">{myBox}</div>
      </div>
    );
  }
}

export default App;
