import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: null,
      tags: null,
    };
  }

  componentDidMount() {
    fetch(`https://conduit.productionready.io/api/articles?limit=10&offset=0`)
      .then((res) => res.json())
      .then((data) => this.setState({ articles: data.articles }))
      .catch((err) => console.log(err));
    fetch(`https://conduit.productionready.io/api/tags`)
      .then((res) => res.json())
      .then((data) => this.setState({ tags: data.tags }))
      .catch((err) => console.log(err));
  }

  render() {
    return (
      <>
        <Header/>

        <section className="hero">
          <h2>conduit</h2>
          <p>A place to share your knowledge.</p>
        </section>

        <section className="main container">
          <div className="row">
            <article className="articles">
              <div>
                <h3>Global Feed</h3>
                <hr />
              </div>

              {this.state.articles
                ? this.state.articles.map((data) => {
                    return (
                      <>
                        <div className="article-meta">
                          <div className="info">
                            <a href="#" className="imgresponsive">
                              <img
                                className="imgauthor"
                                src={data.author.image}
                                alt={data.author.username}
                              />
                            </a>
                            <div className="margin">
                              <a href="#">
                                <div>{data.author.username}</div>
                              </a>
                              <div>{data.updatedAt}</div>
                            </div>
                          </div>
                          <div className="likes">
                            <button>Likes {data.favoritesCount}</button>
                          </div>
                        </div>
                        <div className="article-preview">
                          <h1><a href="#">{data.title}</a> </h1>
                          <p>{data.body}</p>
                          <span>Readmore...</span>
                          <ul className="taglist">
                            {data.tagList
                              ? data.tagList.map((tag) => {
                                  return <a href="#" className="tag">{tag}</a>;
                                })
                              : ""}
                          </ul>
                          <hr />
                        </div>
                      </>
                    );
                  })
                : ""}
            </article>

            <div className="tags">
              <div className="sidebar">
                <h3>Popular Tags</h3>
                <ul className="taglist">
                  {this.state.tags
                    ? this.state.tags.map((tag) => {
                        return <a href="#" className="tag">{tag}</a>;
                      })
                    : ""}
                </ul>
              </div>
            </div>
          </div>
        </section>

        <footer className="footer">
          <a href="#">
            <i className="fa fa-github" aria-hidden="true"></i>
            Fork on GitHub
          </a>
        </footer>
      </>
    );
  }
}

export default App;
